Page._content:
body {
  background-color: #d7ccc8;
  margin: 0px;
  font-family: Lato, "Microsoft Jhenghei", "Hiragino Sans GB", "Microsoft YaHei", sans-serif;
  font-size: 18px;
}
@media (max-width: 860px) {
  body {
    font-size: 16px;
  }
}
article {
  line-height: 1.5em;
}
.highlight {
  line-height: 1.25em;
}
a {
  text-decoration: none;
  color: #3a539b;
}
a:hover {
  text-decoration: underline;
}
#outer-container {
  position: absolute;
  left: 50%;
  width: 960px;
  margin-left: -480px;
  box-shadow: 0 0 2.5em #5d656c;
}
@media (max-width: 960px) {
  #outer-container {
    width: 860px;
    margin-left: -430px;
  }
}
@media (max-width: 860px) {
  #outer-container {
    width: 760px;
    margin-left: -380px;
  }
}
#menu-outer,
#container {
  background-color: #fff;
}
#menu-outer,
#bottom-outer,
#random-index {
  opacity: 0.85;
}
.random-center {
  position: absolute;
  top: 50%;
  left: 50%;
  margin-top: -200px;
  width: 960px;
  margin-left: -480px;
}
@media (max-width: 960px) {
  .random-center {
    width: 860px;
    margin-left: -430px;
  }
}
@media (max-width: 860px) {
  .random-center {
    width: 760px;
    margin-left: -380px;
  }
}
#random-index {
  margin: 20px;
  padding-bottom: 20px;
  text-align: center;
  border-radius: 10px;
  background-color: #ecf0f1;
}
#random-index a {
  text-transform: uppercase;
  padding: 5px;
}
#random-index .title {
  font-size: 28px;
  font-weight: 900;
  font-color: #34495e;
  padding-bottom: 5px;
  cursor: pointer;
}
#random-index .title a {
  color: #34495e;
}
#random-index .title a:hover {
  color: #34495e;
  text-decoration: none;
  background-color: transparent;
}
.sub-title {
  cursor: pointer;
  font-weight: none;
  padding-bottom: 10px;
}
#random-index .index-nav-link {
  list-style: none;
  padding: 5px 0px 5px 0px;
}
#random-index .index-nav-link li {
  display: inline;
  padding-left: 5px;
}
#random-index hr {
  width: 75%;
  color: #7f8c8d;
  margin: 0px auto;
}
.center-field {
  margin: 0 auto;
}
.avatar {
  cursor: pointer;
  margin-top: -70px;
  border: 2px solid #fff;
  border-radius: 50%;
  background-color: #ecf0f1;
  box-shadow: 0 0px 3px 1px rgba(0,0,0,0.3);
}
#menu-outer {
  margin: 0px;
  padding: 0px;
  position: fixed;
  z-index: 1;
  transition-property: all;
  transition-duration: 0.5s;
  transition-timing-function: cubic-bezier(0, 1, 0.5, 1);
}
.slide-up {
  top: -50px;
}
.slide-down {
  top: 0;
}
#menu-inner {
  line-height: 50px;
  height: 49px;
  border-bottom: 1px solid #ddd;
  padding: 0px 10px 0 10px;
  width: 940px;
}
@media (max-width: 960px) {
  #menu-inner {
    width: 840px;
  }
}
@media (max-width: 860px) {
  #menu-inner {
    width: 740px;
  }
}
#menu-inner .left {
  float: left;
}
#menu-inner #brand {
  float: left;
  vertical-align: middle;
  cursor: pointer;
}
#menu-inner #homelink {
  line-height: 32px;
  display: inline-block;
}
#menu-inner #brand a {
  top: 0;
  color: #000;
}
#menu-inner #brand a:hover {
  background-color: #fff;
  text-decoration: none;
}
#menu-list {
  line-style: none;
  display: inline-block;
  float: right;
  padding-left: 0px;
}
#menu-list ul {
  margin: 0px;
}
#menu-list ul li {
  padding: 5px;
  margin: 5px;
  text-align: center;
  display: inline;
}
#menu-list ul li.active {
  font-weight: 900;
}
#menu-list ul li.active a {
  color: #000;
}
#menu-list ul li:hover {
  font-weight: 900;
}
#menu-list ul li a {
  text-decoration: none;
}
#menu-outer #avatar {
  border-radius: 50%;
  border: 2px wihte solid;
  box-shadow: 0 0px 3px 1px rgba(0,0,0,0.3);
  vertical-align: middle;
  width: 32px;
  height: 32px;
}
#bottom-outer {
  color: #888;
  text-align: center;
  padding: 10px;
  padding-top: 40px;
  background-color: #fff;
  font-size: 14px;
}
@media (max-width: 960px) {
  #bottom-outer {
    font-size: 12px;
  }
}
#container {
  margin-top: 50px;
}
#content-outer {
  padding: 10px;
}
.tag-cloud-tags {
  text-align: center;
}
.tag-cloud-tags a {
  padding: 3px;
}
.tag-cloud-tags a:hover {
  text-decoration: none;
  background-color: #bbb;
  color: #000;
}
.center-title {
  text-align: center;
}
.category-list {
  text-align: center;
  padding-left: 0px;
}
ul.category-list {
  list-style: none;
}
ul.category-list li {
  display: inline-block;
  margin: 10px;
}
ul.category-list li a {
  padding: 8px;
  margin: 8px;
  background-color: #eee;
  color: #000;
}
ul.category-list li a:hover {
  background-color: #ddd;
  text-decoration: none;
}
.archive-year-title {
  margin: 10px 0 5px 0;
}
.posts-count {
  font-size: 12px;
  font-weight: normal;
  color: #888;
  font-style: italic;
}
.post-title-item {
  margin-left: 20px;
  padding-left: 5px;
  border-left: 2px #bbb solid;
}
.post-title-item:hover {
  border-left: 4px #1e8bc3 solid;
}
.post-title-item p {
  margin-top: 2px;
}
#pagination {
  margin-top: 20px;
  text-align: center;
}
#pagination a {
  padding: 5px;
  background-color: #eee;
  line-height: 28px;
}
#pagination a:hover {
  background-color: #ddd;
  color: #1f3a93;
  text-decoration: none;
}
#btn-view {
  color: #c8f7c5;
  text-align: center;
  background-color: #1e824c;
  border-radius: 50%;
  line-height: 40px;
  display: block;
  position: fixed;
  top: 90%;
  margin-left: 20px;
  height: 40px;
  width: 40px;
  cursor: pointer;
  opacity: 0.25;
}
#btn-view:hover {
  opacity: 0.75;
  box-shadow: 0 0px 10px 5px rgba(46,204,113,0.9);
}
.page-title-sub {
  color: #888;
  font-size: 14px;
}
@media (max-width: 960px) {
  .page-title-sub {
    font-size: 12px;
  }
}
.page-title-sub span {
  margin-right: 5px;
}
article img {
  max-width: 940px;
}
@media (max-width: 960px) {
  article img {
    max-width: 840px;
  }
}
@media (max-width: 860px) {
  article img {
    max-width: 740px;
  }
}
#user-card {
  text-align: center;
  color: #333;
  display: none;
}
#user-card .avatar {
  width: 96px;
  height: 96px;
  margin-top: 0px;
}
#user-card ul {
  list-style: none;
  padding: 0px;
}
#user-card ul li {
  display: inline;
  margin: 0px;
  padding: 0px 3px;
}
#show-menu {
