}
.social-icon li {
  display: inline;
}
.social-icon li a {
  color: #808080;
}
.social-icon li a i {
  font-size: 25px;
}
.social-icon li a:hover {
  text-decoration: none;
}
.douban:hover {
  color: #072;
}
.twitter:hover {
  color: #1da1f2;
}
.linkedin:hover {
  color: #333;
}
.weibo:hover {
  color: #fa7d3c;
}
.google-plus:hover {
