  color: #0e78e7;
}
.lofter:hover {
  color: #444;
}
.quora:hover {
  color: #b92b27;
}
.rss:hover {
  color: #d35400;
}
.side-navigate {
  position: fixed;
  top: 60%;
  width: 100%;
  z-index: 2;
  pointer-events: none;
}
.side-navigate .item {
  display: table-cell;
}
.side-navigate .item-icon:hover {
  opacity: 1;
  background-size: 40px 40px;
}
.side-navigate .item-icon {
  pointer-events: auto;
  opacity: 0.55;
  padding: 20px;
  border-radius: 50%;
  border: 1px #52b3d9 solid;
  width: 30px;
  height: 30px;
  background-repeat: no-repeat;
  background-position: center;
  background-size: 30px 30px;
}
.side-navigate a:hover + div {
  display: block;
}
.side-navigate a:hover {
  display: block;
}
.side-navigate .prev .item-icon {
  background-image: url("prev.png");
  float: left;
}
.side-navigate .prev {
  display: inline-block;
  left: 0;
}
.side-navigate .item-title {
  display: none;
  padding: 10px;
  max-width: 300px;
  vertical-align: center;
  background-color: #52b3d9;
  height: 52px;
}
.side-navigate .next .item-icon {
  background-image: url("next.png");
  float: right;
}
.side-navigate .prev .item-title {
  margin-left: 70px;
}
.side-navigate .next .item-title {
  margin-right: 70px;
}
.side-navigate .next {
  display: inline-block;
  float: right;
  right: 0;
}
.fancybox-thumb:hover {
  text-decoration: none;
}
.fancybox-thumb img {
  max-width: 120px;
  max-height: 120px;
  padding: 3px;
  border-radius: 5%;
  border: 1px solid #ddd;
}
.caption {
  display: block;
  text-align: center;
  font-size: 14px;
  color: #999;
}
.random-toc-area {
  position: fixed;
  width: 200px;
  right: 0px;
  top: 0px;
  display: block;
}
@media (max-width: 1360px) {
  .random-toc-area {
    display: none;
  }
}
.random-toc {
  background: #444;
  color: #bbb;
  height: 500px;
  margin-top: 0px;
  font-size: 12px;
  height: 100%;
  overflow-y: auto;
}
.random-toc ol {
  padding-left: 15px;
}
.random-toc h2 {
  text-align: center;
}
.random-toc a {
  color: #bbb;
  display: block;
}
.toc-text {
  display: block;
}
.toc-text:hover {
  background-color: #666;
}
.btn-hide-toc {
  border: none;
  z-index: 1;
  display: block;
  width: 100%;
  background-color: #666;
  height: 30px;
  color: #bbb;
}
.btn-hide-toc:hover {
  background-color: #888;
}
figure.highlight {
  margin: 0px -10px 0px -10px;
}
figure.highlight td.gutter {
  padding-right: 20px;
}
figure.highlight .line {
  white-space: pre-wrap;
}
code {
  font-family: Consolas, Monaco, Courier New, Lucida Console;
  background-color: #ddd;
  font-style: italic;
  padding-left: 12px;
  padding-right: 12px;
}
table .line {
  font-family: Consolas, Monaco, Courier New, Lucida Console;
}
blockquote {
  background-color: #eee;
  padding: 2px 10px;
  border-left: 2px solid #999;
}
@media (max-width: 1060px) {
  .side-navigate {
    display: none;
  }
  .jiathis_style {
    display: none;
    visibility: hidden;
  }
}
@media (max-width: 760px) {
  #outer-container,
  .random-center,
  #menu-outer {
    width: 100%;
  }
  #outer-container {
    left: 0%;
    margin-left: 0px;
  }
  #menu-inner {
    width: 95%;
    padding: 0 2%;
  }
  .post-title-item {
    margin-left: 3px;
  }
  #show-menu {
    display: inline-block;
  }
  article img {
    width: 95%;
  }
  #menu-list {
    display: none;
    position: absolute;
    margin-top: 50px;
    background-color: #fff;
    opacity: 0.85;
    width: 100%;
  }
  #menu-list ul {
    padding-left: 0px;
  }
  #menu-list ul li {
    text-align: center;
    display: block;
  }
  .highlight {
    margin-left: 0px;
    margin-right: 0px;
  }
}
@media (max-width: 760px) and (max-width: 480px) {
  .random-center {
    position: static;
    left: 0;
    margin-left: 0px;
    width: 100%;
    margin-top: 30%;
  }
  #random-index .index-nav-link li {
    display: block;
  }
  #bottom-outer,
  #random-index {
    opacity: 0.95;
  }
  #menu-outer {
    opacity: 0.95;
  }
}
@media (max-width: 760px) and (max-height: 480px) {
  .random-center {
    position: static;
    top: 0;
    left: 0;
    margin-left: 0px;
    margin-top: 95px;
    width: 100%;
  }
  #random-index .index-nav-link li {
    display: block;
    padding: 0px 0px;
  }
}
@media (max-width: 760px) and (max-height: 320px) {
  .random-center {
    margin-top: 75px;
  }
}

